mapper.select:
SELECT * FROM offer 
	WHERE area_code=#{areaCode}
		AND branch_code	=#{branchCode}
		AND scc_code	=#{sccCode}
		AND program		=#{program};

mapper.update:
UPDATE offer 
	SET monthly_oper = monthly_oper+#{monthlyOper} 
	WHERE code=#{offerCode};

UPDATE offer
area_code =#{areaCode}, branch_code =#{branchCode}, scc_code =#{sccCode}, program =#{program}, monthly_oper=#{monthlyOper}, active_user =#{activeUser},
WHERE code=#{code};
